const icons=document.querySelector("meta[name=icons]").getAttribute("content").split(",").map(e=>e.split("-")[2]).filter((e,l,i)=>i.indexOf(e)===l);console.log(JSON.stringify(icons));const template=(l="regular",i="black_white",n="s")=>`
  <h1>H2D2 Shopicons</h1>
  <fieldset>
    <legend>Style</legend>
    ${["light","regular","bold","filled"].map(e=>`<label><input type="radio" name="style" value="${e}" ${e===l?"checked":""}> ${e}</label>`).join(" ")}
  </fieldset>
  <fieldset>
    <legend>Größe</legend>
    ${["s","m","l","xl"].map(e=>`<label><input type="radio" name="size" value="${e}" ${e===n?"checked":""}> ${e}</label>`).join(" ")}
  </fieldset>
  <fieldset>
    <legend>Farbe</legend>
    ${["black_white","gray_white","white_black"].map(e=>`<label><input type="radio" name="color" value="${e}" ${e===i?"checked":""}> ${e}</label>`).join(" ")}
  </fieldset>
  <div class="icons ${i}">
    ${icons.map(e=>`<div class="icon">
        <shopicon-${l}-${e} size="${n}"></shopicon-${l}-${e}>
        <small>${e}</small>
      </div>`).join("\n")}
  </div>
  <style>
    body { padding: 0; margin: 0; font-family: "Helvetica Neue", Helvetica, Arial }
    h1 { margin: 1.5rem 2rem; }
    fieldset { display: inline-block;  margin: 1.5rem 2rem; border: 1px solid black; border-radius: 3px; }
    .icon { padding: 0.5em; margin-bottom: 0.4em; text-align: center; width: 70px; height: 85px; display: flex; align-items: center; justify-content: center; flex-direction: column; }
    .icon small { padding-top: 0.5em; max-width: 100%; overflow: hidden; text-overflow: ellipsis; display: block; self-justify: end; font-size: 0.7rem; height: 1.2em; }
    [data-shopicon] { margin: 0 auto; }
    .icons { display: flex; flex-wrap: wrap; padding: 1.5rem 2rem; }
    .icons.black_white { color: black; background-color: white; }
    .icons.gray_white { color: lightgrey; background-color: white; }
    .icons.white_black { color: white; background-color: black; }
  </style>
`,main=document.createElement("main");function changeHandler(){var e=document.querySelector('input[name="style"]:checked').value,l=document.querySelector('input[name="color"]:checked').value,i=document.querySelector('input[name="size"]:checked').value;main.innerHTML=template(e,l,i),addEvents()}function addEvents(){var e=document.querySelectorAll("input[type=radio]");Array.prototype.forEach.call(e,function(e){e.addEventListener("change",changeHandler)})}main.innerHTML=template(),document.body.appendChild(main),addEvents();
